%FT2dexp% shows the real and imaginary parts and the phase of a linear complex exponential% (the Fourier kernel)n=128; %array sizexcoef=2, ycoef=0, % location of delta function% change these numbers to get different fringe patternsndc=fix(n/2)+1;xvec=[1:n]-ndc;[X,Y]=meshgrid(xvec,xvec);G = exp(i*2*pi*(xcoef*X + ycoef*Y)/n);figure(3);showp(real(G)-min(real(G(:))),1,2); % the real partfigure(4);showp(imag(G)-min(imag(G(:))),1,2); % the imaginary partfigure(5);showp(arg(G)+pi,1,2); % the phasereturn