function circ=circleshade(n,rad,rcent,ccent);%function circ=circleshade(n,rad,rcent,ccent);% n=[nr,nc] is acceptable%circle of radius rad in nxn array;%edges shaded by approx area of pixel covered by circle% default centers = dc (in center)if length(n)==1,	nr=n; nc=n;else,	nr=n(1);nc=n(2);	endnrdc=fix(nr/2)+1;ncdc=fix(nc/2)+1;if exist('ccent')~=1, ccent=ncdc; endif exist('rcent')~=1, rcent=nrdc; endrramp=[1:nr];cramp=[1:nc];[X,Y]=meshgrid(cramp,rramp);circ=sqrt((Y-rcent).^2+(X-ccent).^2); % note: Y = rows, X = columns\radph=rad+0.5;circ=radph-circ;circ=min(max(circ,0),1);