function c=shiftwrap(a,row,col)%function c=shiftrwap(a,row,col)%shift with wrap-around% J.R. Fienup modified 9/14/01 to speed up[nr,nc]=size(a);% b=zeros(nr,nc);c=b;if col~=0,  if col>=0,b=a(:,[nc-col+1:nc,1:nc-col]);    else b=a(:,[1-col:nc,1:-col]);  endelse b=a;endif row~=0  if row>=0,c=b([nr-row+1:nr,1:nr-row],:);    else c=b([1-row:nr,1:-row],:);  endelse c=b;end