% showalias2.m;% read in and Fourier transform an image% taken with a digital camera.% Aliasing in the Fourier domain is noted near the top and bottom% of the Fourier transform% J.R. Fienup 9/16/04a=double(imread('aliasedimage.tif'));aft=abs(fftshift(fftn(a)));figure(1);showp(a,1,1);figure(2);showp(aft.^.3,4,1)return% another case: downsample further (making it more undersampled)[nra,nca]=size(a);c=a(1:2:nra,1:2:nca);cft=fftshift(fftn(fftshift(c)));figure(3);showp(c,1,1);figure(4);showp(cft.^.3,4,1)% another case: downsample with 2x2 integration (like using larger pixels)d=a(1:2:nra,1:2:nca)+a(2:2:nra,1:2:nca)+a(1:2:nra,2:2:nca)+a(2:2:nra,2:2:nca);d=d/4;dft=fftshift(fftn(fftshift(d)));figure(5);showp(d,1,1);figure(6);showp(dft.^.3,4,1)