function showp(a,fps,magfactor)% function showp(a,fps,magfactor)% shows image for use in papers - with no boarders% if magfoctor is given, then the dimensions on the screen% is in 1 image pixel = magfactor screen pixels (otherwise scales to existing window)% J.R. Fienup 9/2/99if exist('fps')~=1, fps=1;endshow(a,fps)axis off% axes('Position',[0 0 1 1]); % or equivalentlyset(gca,'Position',[0 0 1 1]);if exist('magfactor')==1,	pos=get(gcf,'Position');	pos(3:4)=round(magfactor.*[size(a,2),size(a,1)]);	set(gcf,'Position',pos)end